import React from "react";
import "./Book.css"
import Book from "./Book"
const books = [
    {
        title:"Crazy",
        author: '막스 베르스타펜',
        coverImage: "data:image/jpeg;base64,"
    },
    {
        title:"Goat",
        author: '샤를 르끌레르',
        coverImage:"data:image/jpeg;base64,"
    }
]

function BookList(){
    return(
        <div className={"bookListWrapper"}>
            {
                books.map((book)=>{
                    return(
                        <Book
                            title={book.title}
                            author={book.author}
                            coverImage={book.coverImage}

                        />
                    )
                })
            }
        </div>
    )
}

export default BookList;